import React, { useState } from 'react'
import Blog from './Blog'
import blogService from '../services/blogs'


const BlogForm = (props) => {
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('') 
    const [url, setURL] = useState('') 
    const [blogVisibility, setVisibility] = useState(false)
    const addBlog = (event) => {
      event.preventDefault()
      try {
      const blogObject = {
        title: title,
        author: author,
        url: url
      }
      blogService
      .create(blogObject)
      .then(returnedBlog => {
        if (returnedBlog.split(':')[0]=== 'error') {
          if(title === '') {
            props.setErrorMessage('title missing')
          setTimeout(() => { 
            props.setErrorMessage(null)
          },5000)
          } else {
            props.setErrorMessage('URL missing')
          setTimeout(() => { 
            props.setErrorMessage(null)
          },5000)
          }
        } else {
        props.setBlogs(props.blogit.concat(returnedBlog))
        props.setMessage(`new Blog ${title} added`)
        setTimeout(() => {
          props.setMessage(null)
        },5000)
        setTitle('')
        setAuthor('')
        setURL('')
      }
      })
    }  catch (error) {
      console.log(error)
     } 
 }

 const BlogiForm = () => {
  const hideWhenVisible = { display: blogVisibility ? 'none' : '' }
  const showWhenVisible = { display: blogVisibility ? '' : 'none' }

  return (
    <div>
      <h2>create new</h2>
        <form onSubmit={addBlog}>
      <div>
        title:
          <input
          type="text"
          value={title}
          name="title"
          onChange={({ target }) => setTitle(target.value)}
        />
      </div>
      <div>
        author:
          <input
          type="text"
          value={author}
          name="title"
          onChange={({ target }) => setAuthor(target.value)}
        />
      </div>
      <div>
        url:
          <input
          type="text"
          value={url}
          name="title"
          onChange={({ target }) => setURL(target.value)}
        />
      </div>
      <button type="submit">create</button>
    </form>
    </div>
  )
 }
    return (
        <div>
        <h2>blogs</h2>
        {props.name} logged in <button onClick={props.handleLogout}>logout</button>
        <br /> 
        <br /> 
        <Togglale ButtonLabel='add Blog'>
          <BlogiForm
          title={title}
          author={author}
          url={url}>
          </BlogiForm>
        </Togglale>
           <BlogiForm/>
        {props.blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )}
      </div>
    )
}

export default BlogForm
