import React from 'react'
import '../index.css'
import { useSelector } from 'react-redux'

/*const Notification = ({ message }) => {
  if (message === null) {
    return null
  } else {
    return (
      <div className="finding">
        {message}
      </div>
    )
  }


}

export default Notification*/

const Notification = () => {
  const notification = useSelector(store => store.notification)



  const style = {
    border: 'solid',
    padding: 10,
    borderWidth: 1
  }

  if (notification === '') {
    return <div/>
  } else {
    return (
      <div style={style}>
        {notification}
      </div>
    )
  }

}

export default Notification
