import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import { likeBlog, deleteBlog } from '../reducers/blogReducer'
import blogService from '../services/blogs'
import {
  BrowserRouter as Router,
  Switch, Route
} from 'react-router-dom'

const Blog = ({ blog }) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  const dispatch = useDispatch()

  const [isVisible, setVisible] = useState(false)
  const [userID, setUserID] = useState('')

  const editVisibility = () => {
    setVisible(!isVisible)
  }

  useEffect(() => {
    const maybeUser = window.localStorage.getItem('user')
    if (maybeUser !== null) {
      setUserID(maybeUser.split(',')[3].split('"')[3])
    }
  }, [])

  const addLike = async () => {
    const allBlogs = await blogService.getAll()
    const filtered = allBlogs.filter(blogi => blogi.id === blog.id)[0]
    const blogObject = {
      user: filtered.user.id,
      likes: filtered.likes + 1,
      author: filtered.author,
      title: filtered.title,
      url: filtered.url,
      id: filtered.id
    }
    blogService.addLike(blogObject)
    dispatch(likeBlog(blogObject))
  }

  const Delete = async () => {
    if (window.confirm(`Remove ${blog.title}, by ${blog.author}`)) {
      blogService
        .remove(blog)

      dispatch(deleteBlog(blog))
    }
  }

  const DeleteButton = () => {

    return (
      <div>
        <button onClick={Delete}>Delete</button>
      </div>
    )
  }




  return (
    <div>
      {isVisible ? (
        <div style={blogStyle}>
          {blog.title} <button onClick={editVisibility}>hide</button>
          <Link to="/blogs/:id"></Link>
          <div>likes {blog.likes} <button onClick={addLike}>like!</button></div>
          <div>{blog.author}</div>
          {userID === blog.user.id && DeleteButton()}
        </div>
      ) : (
        <div style={blogStyle}>
          {blog.title} {blog.author}
          <button onClick={editVisibility}>view</button>
        </div>
      )}
    </div>
  )
}


export default Blog
