import blogs from '../services/blogs'

export const initializeBlogs = () => {
  return async dispatch => {
    const allBlogs = await blogs.getAll()
    dispatch ({
      type: 'INITIALIZE',
      data: { allBlogs }
    })
  }
}

export const addBlog = ({ blog }) => {
  return async dispatch => {
    const blogi = await blogs.create(blog)
    dispatch ({
      type: 'ADDBLOG',
      data: { blogi }
    })
  }
}

const blogReducer = (state = [], action) => {
  switch (action.type) {
  case 'ADDBLOG':
    return action.data.blogi
  case 'INITIALIZE':
    return action.data.allBlogs
  default:
    return state
  }
}

export default blogReducer
