import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import Login from './components/Login'
import loginUser from './services/loginUser'
import ErrorNotification from './services/errorMessage'
import Notification from './services/notification'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('') 
  const [password, setPassword] = useState('') 
  const [user, setUser] = useState(null)
  const [errorMessage, setErrorMessage] = useState(null)
  const [message, setMessage] = useState(null)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  const editUserName = (event) => {
    setUsername(event.target.value)
  }

  const editPassword = (event) => {
    setPassword(event.target.value)
  }

  const editUser = async (event) => {

    try {
      const user = await loginUser.loginUser({
        username, password,
      })
      setUser(user)
      setMessage(user)
      console.log(user)
      setUsername('')
      setPassword('')

    } catch (e) {
      setErrorMessage('wrong credentials')
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
      console.log('vittu')
    }
  }


    return (
      <div>
        <div>
        <h2>Login</h2>
        <ErrorNotification errorMessage={errorMessage}/>
        <Notification message={message}/>
        <Login username={username} editUserName={editUserName} password={password} editPassword={editPassword} editUser={editUser}/>
        </div>

        <div>
        <h2>blogs</h2>
        {blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )}
      </div>

      </div>
    )

}

export default App
