import React, { useState, useEffect } from 'react'
import BlogForm from './components/BlogForm'
import Blog from './components/Blog'
import blogs from './services/blogs'
import loginUser from './services/loginUser'
import LoginForm from './components/LoginForm'
import ErrorNotification from './components/errorMessage'
import Notification from './components/notification'
import { useDispatch, useSelector } from 'react-redux'
import { setErrorNotification } from './reducers/errorNotificationReducer'
import { initializeBlogs } from './reducers/blogReducer'
import { loginUseri, logoutUser } from './reducers/loginReducer'
import Users from './components/Users'
import  { initializeUsers } from './reducers/userReducer'
import {
  BrowserRouter as Router,
  Switch, Route, Link
} from 'react-router-dom'


const App = () => {
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [name, setName] = useState('')
  const dispatch = useDispatch()
  const blogit = useSelector(state => state.blogReducer)
  const login = useSelector(state => state.login)


  useEffect(() => {
    const maybeUser = window.localStorage.getItem('user')
    if (maybeUser !== null) {
      setName(maybeUser.split(',')[2].split('"')[3])
      blogs.setToken(maybeUser.split(',')[0].split('"')[3])
      dispatch(loginUseri(JSON.stringify(maybeUser)))
    }
    dispatch(initializeBlogs())
    dispatch(initializeUsers())
  }, [])


  const handleLogin = async (event) => {
    event.preventDefault()

    try {
      const user = await loginUser.loginUser({
        username, password,
      })
      setName(login.name)
      blogs.setToken(login.token)
      window.localStorage.setItem(
        'user', JSON.stringify(login)
      )
      dispatch(loginUseri(JSON.stringify(login)))
      setUsername('')
      setPassword('')

    } catch (e) {
      dispatch(setErrorNotification('wrong credentials',5))
    }
  }

  const handleLogout = async() => {
    window.localStorage.removeItem('user')
    dispatch(logoutUser())
    setName(null)
  }



  return (
    <div>
      <Router>
        <ErrorNotification/>
        <Notification/>
        {login === null ?
          <LoginForm handleLogin={handleLogin} username={username} password={password} setUsername={setUsername} setPassword={setPassword} /> :
          <BlogForm name={name} handleLogout={handleLogout}/>}
        <Switch>
          <Route path="/">
            {blogit !== undefined && login !== null?
              blogit.sort((a, b) => b.likes - a.likes).map(blog =>
                <Blog key={blog.id} blog={blog}/>
              ): null}
          </Route>
        </Switch>
      </Router>
    </div>
  )

}

export default App
