import React, { useState } from 'react'
const Blog = ({ blog }) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }
  
  const [isVisible, setVisible] = useState(false)

    const editVisibility = (event) => {
      console.log(event.target.value)
      setVisible(event.target.value)
    }
  return (
    isVisible ? (
      <div style={blogStyle}>
        {blog.title} <button value='false' onClick={editVisibility}>hide</button>
        <div>{blog.url}</div>
        <div>likes {blog.likes}</div>
        <div>{blog.author}</div>
      </div>
    ) : (
  <div style={blogStyle}>
    {blog.title} {blog.author}  
    <button value='true' onClick={editVisibility}>view</button>
  </div>
    )
  )
  }


export default Blog
