export const loginUser = (user) => {
  return async dispatch => {
    dispatch({
        type: 'LOGIN',
        data: { user }
    })
  }
}

export const logoutUset = (user) => {
    return async dispatch => {
      dispatch({
          type: 'LOGOUT'
      })
    }
  }

const loginReducer = (state = null, action) => {
  switch (action.type) {
  case 'LOGIN':
    return action.data.user
  case 'LOGOUT':
    return null
  case 'INITIALIZEUSER':
    return action.data.user
  default:
    return state
  }
}

export default loginReducer
