import React, { useState } from 'react'

const Blog = ({ blog }) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const [isVisible, setVisible] = useState(false)

    const editVisibility = () => {
      setVisible(!isVisible)
    }

  return (
    <div>
    {isVisible ? (
      <div style={blogStyle}>
        {blog.title} <button onClick={editVisibility}>hide</button>
        <div>{blog.url}</div>
        <div>likes {blog.likes}</div> <button onClick={addLike}>like!</button>
        <div>{blog.author}</div>
      </div>
    ) : (
  <div style={blogStyle}>
    {blog.title} {blog.author}  
    <button onClick={editVisibility}>view</button>
  </div>
    )}
    </div>
  )
  }


export default Blog
