import React, { useState } from 'react'
import blogService from '../services/blogs'

const Blog = ({ blog }) => {

  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  const [isVisible, setVisible] = useState(false)

    const editVisibility = () => {
      setVisible(!isVisible)
    }

    const addLike = async () => {
      const allBlogs = await blogService.getAll()
      const filtered = allBlogs.filter(blogi => blogi.id === blog.id)
      console.log(filtered)
      const blogObject = {
        user: filtered.user._id,
        likes: filtered.likes + 1,
        author: filtered.author,
        title: filtered.title,
        url: filtered.url
      } 
      blogService
      .addLike(blogObject)
      .then((returnedBlog) => {
        console.log(returnedBlog)
      })
    }

  return (
    <div>
    {isVisible ? (
      <div style={blogStyle}>
        {blog.title} <button onClick={editVisibility}>hide</button>
        <div>{blog.url}</div>
        <div>likes {blog.likes} <button onClick={addLike}>like!</button></div>
        <div>{blog.author}</div>
      </div>
    ) : (
  <div style={blogStyle}>
    {blog.title} {blog.author}  
    <button onClick={editVisibility}>view</button>
  </div>
    )}
    </div>
  )
  }


export default Blog
