import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import Login from './components/Login'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('') 
  const [password, setPassword] = useState('') 
  const [isLoggedIn, setLoggedIn] = useState(false)

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  const editUserName = (event) => {
    event.preventDefault()
    setUsername(event.target.value)
  }

  const editPassword = (event) => {
    event.preventDefault()
    setPassword(event.target.value)
  }

  const editLoggedIn = (event) => {
    event.preventDefault()
    setLoggedIn(event.target.value)
  }


    return (
      isLoggedIn ? (
        <div>
        <h2>blogs</h2>
        {blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )}
      </div>
      ) : (
        <div>
        <h2>Login</h2>
        <Login username={username} editUserName={editUserName} password={password} editPassword={editPassword} setLoggedIn={editLoggedIn}/>
        </div>
      )
    )

}

export default App
